-- Use this file to write the SQL needed to create the basic tables for your databse
-- These tables are where you are putting the data generated by the bar as well as the relevant data from the cocktails database.

CREATE TABLE IF NOT EXISTS all_bars_transaction_data 
    ([timestamp] TEXT
    , [drink] TEXT
    , [amount] REAL
    , [branch] TEXT
);
CREATE TABLE IF NOT EXISTS drinks_and_glasses
    ([drink] TEXT
    , [glass] TEXT
);
CREATE TABLE IF NOT EXISTS bar_stock_data
    ([glass_type] TEXT
    , [stock] INTEGER
    , [bar] TEXT
);